import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./ui-slice";

const cartSlice=createSlice({
    name:'cart',
    initialState:{
        items:[],
        totalQuantity:0,
        changed:false,
    },
    reducers:{
        replaceCart(state,action) {
            state.totalQuantity = action.payload.totalQuantity;
            state.items = action.payload.items;
          },
        addItemToCart(state,action){
            const newItem=action.payload;
            console.log("new Item:"+newItem);
            const existingItem=state.items.find(item=>item.id === newItem.id);
            console.log("existingItem:"+existingItem);
            state.totalQuantity++;
            state.changed=true;
            console.log(state.totalQuantity);
            if(!existingItem){
                state.items.push({
                    id:newItem.id,
                    price:newItem.price,
                    quantity:1,
                    totalPrice:newItem.price,
                    name:newItem.title
                });
            }else{
                existingItem.quantity++;
                console.log("existingItem.quantity"+existingItem.quantity);
                existingItem.totalPrice=existingItem.totalPrice+newItem.price;
                console.log("existingItem.totalPrice"+existingItem.totalPrice);
            }
        },
        removeItemCFromCart(state,action){
            const id=action.payload;
            const existingItem=state.items.find(item=>item.id === id);
            console.log("existingItem in remove"+existingItem)
            state.totalQuantity--;
            // state.changed=true;
            if(existingItem.quantity === 1){
                state.items= state.items.filter(item=>item.id !== id);
                console.log("qua 1"+state.items)
            }else{
                existingItem.quantity--;
                //existingItem.totalPrice=existingItem.totalPrice-existingItem.price;
            }
        }
    }
});

export const cartActions=cartSlice.actions;
export default cartSlice;
